  KEY `idx_role_log_operator` (`operator_user_id`),
  KEY `idx_role_log_type` (`operation_type`),
  KEY `idx_role_log_created` (`created_at`),
  KEY `fk_role_log_old_role` (`old_role_id`),
  KEY `fk_role_log_new_role` (`new_role_id`),
  CONSTRAINT `fk_role_log_new_role` FOREIGN KEY (`new_role_id`) REFERENCES `roles` (`role_id`) ON DELETE SET NULL ON UPDATE CASCADE,
  CONSTRAINT `fk_role_log_old_role` FOREIGN KEY (`old_role_id`) REFERENCES `roles` (`role_id`) ON DELETE SET NULL ON UPDATE CASCADE,
  CONSTRAINT `fk_role_log_operator` FOREIGN KEY (`operator_user_id`) REFERENCES `users` (`user_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `fk_role_log_target` FOREIGN KEY (`target_user_id`) REFERENCES `users` (`user_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='角色权限变更日志表（用于审计和追踪所有权限变更操作）';

